graphql.query:
query {
    users { id, firstName },
    posts { id, title, content },
    memberTypes { id, discount, monthPostsLimit },
    profiles { id, userId, country, avatar, memberTypeId }
}

query(
    $userId: ID,
    $postId: ID,
    $memberTypeId: String,
    $profileId: ID
) {
    user(id: $userId) { id, email },
    post(id: $postId) { id, title, content },
    memberType(id: $memberTypeId) { id, discount, monthPostsLimit },
    profile(id: $profileId) { id, country, avatar }
}

query {
    users{
        id,
        email,
        firstName,
        subscribedToUserIds,
        posts { id, title, content },
        profile { id, country },
        memberType { id, discount },
        userSubscribedTo { id, userSubscribedTo{id, subscribedToUser{id, subscribedToUser{id} }}, subscribedToUser{id} },
        subscribedToUser { id }
    }
}

query(
    $userId: ID
) {
    user(id: $userId) { 
        id,
        email,
        firstName,
        posts { id, title, content },
        profile { id, country },
        memberType { id, discount },
        userSubscribedTo { id }
    }
}

# Create user
mutation {
    createUser(data: {
        firstName: "John",
        lastName: "Doe",
        email: "johndoe1@example.com"
    }) {
        firstName
        lastName
        email,
        id
    }
}

mutation($userId: ID) {
    createProfile(
        data: {
                avatar: "no_logo",
                sex: "male",
                birthday: "01.01.2001",
                country: "Austria",
                street: "Street 1",
                city:"Wise",
                memberTypeId: "basic",
                userId: $userId,
        }
    ) {
        sex
    }
}

mutation($id: ID!) {
    updateProfile(
        id: $id,
        data: {
            avatar: "https://example.com/avatar.png",
            sex: "female",
            birthday: "1990-01-01",
            country: "United States",
            street: "123 Example Street",
            city: "New York",
            memberTypeId: "basic"
        }
    ) {
        avatar
    }
}

# Create post
mutation($userId: String) {
    createPost(data: {
        title: "MyPost",
        content: "Contentt",
        userId: $userId
    }) {
        title
    }
}

mutation($id: ID!) {
    updateUser(
        id: $id,
        data: {
            email: "someOther@mail.ru"
        }
    ) {
        email,
        id
    }
}

mutation($id: ID!) {
    updatePost(
        id: $id,
        data: {
            title: "New title for post",
            content: "Updated post content"
        }
    ) {
        title
    }
}

mutation($id: String!) {
    updateMemberType(
        id: $id,
        data: {
            discount: 10,
            monthPostsLimit: 10,
        }
    ) {
       discount,
       monthPostsLimit
    }
}

mutation(
    $subscriptionId: ID!,
    $userId: ID!
) {
    subscribe(subscriptionId: $subscriptionId, userId: $userId) {
        id,
        email,
        firstName,
        posts { id, title, content },
        profile { id, country },
        memberType { id, discount },
        userSubscribedTo { id }
    }
}

mutation(
    $subscriptionId: ID!,
    $userId: ID!
) {
    subscribe(subscriptionId: $subscriptionId, userId: $userId) {
        id,
        email,
        firstName,
        posts { id, title, content },
        profile { id, country },
        memberType { id, discount },
        userSubscribedTo { id }
    }
}

